function beforeCancelProcess(colleagueId,processId){
  var state = getValue('WKNumState');
  if(state == 12 || state == 62 ||  state == 50 || state == 20 || state == 55  || state == 66)
    throw ("Não é possível cancelar esse processo.");
}

function afterTaskSave(colleagueId,nextSequenceId,userList){

	var sequenceId = getValue("WKCurrentState");
	var numProces = getValue("WKNumProces");
	log.warn("--Debbug-- Solicitacao Compras - after task save");
	log.warn("--Debbug-- var NextsequenceId: " + nextSequenceId)
	log.warn("--Debbug-- var sequenceId: " + sequenceId)
	log.warn("--Debbug-- var colleagueId: " + colleagueId);

	if ( nextSequenceId == 38 ) {
		var nome = hAPI.getCardValue("responsavel");
		var parecer = hAPI.getCardValue("parecer");
		var status = hAPI.getCardValue("aprovado")+' pelo Gestor Imediato';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 16) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Em Cotação';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 20) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Cotação Cadastrada';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 50) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Cotação em Análise';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 14) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Fim - Em Cotação';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 18) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Fim - Retorno Fornecedor';
		addfilho(nome, parecer, status);
	} else
	if (nextSequenceId == 48) {
		var nome = hAPI.getCardValue("nomeSolicitante");
		var parecer = 'Movimentação Protheus';
		var status = 'Fim - Cotação em Análise';
		addfilho(nome, parecer, status);
	}
}
//funcao para pegar o nome do usuario
function getUsuario(user) {

	var c1 = DatasetFactory.createConstraint("colleaguePK.colleagueId", user,user, ConstraintType.MUST);
	var datasetUser = DatasetFactory.getDataset("colleague", null, [ c1 ], null);
	var colleague = '';
	if (datasetUser.rowsCount > 0) {
		colleague =  datasetUser.getValue(0, "colleagueName");
	}
	return colleague;
}

function addfilho(nome, problema, status) {

	var today = new Date();
	var year = today.getFullYear();
	var month = (today.getMonth() + 1) < 10 ? '0' + (today.getMonth() + 1)	: (today.getMonth() + 1);
	var day = today.getDate() < 10 ? '0' + today.getDate() : today.getDate();
	var hour = today.getHours() < 10 ? '0' + today.getHours() : today.getHours();
	var minute = today.getMinutes() < 10 ? '0' + today.getMinutes() : today.getMinutes();
	var second = today.getSeconds() < 10 ? '0' + today.getSeconds() : today.getSeconds();
	var currentHour = hour + ":" + minute + ":" + second;
	var currentDate = day + '/' + month + '/' + year;
	var currentTime = currentDate + "  " + currentHour;

	var childData = new java.util.HashMap();

	childData.put("histUsuario", nome);
	childData.put("histData", currentTime);
	childData.put("histInt", problema);
	childData.put("histStatus", status);

	hAPI.addCardChild('tableHistorico', childData);
}

function servicetask62(attempt, message) {
	log.warn("--Debbug-- Solicitação de Compras - Gerar Pedido de compra");
	var listaEmpresas = ServiceManager.getService('WSPROTHEUS');

	var serviceLocator;
	var serviceProdutos
	try{
		serviceLocator = listaEmpresas.instantiate('br.edu.unievangelica.compras2._8085.WSINTFLUIG');
 		serviceProdutos = listaEmpresas.instantiate('br.edu.unievangelica.compras2._8085.ObjectFactory');
	}catch(erro){
		log.warn("--Debbug-- Algo de Errado não está certo: " + erro);
	}

	log.warn("--Debbug-- listaCliente: " +listaEmpresas);

	log.warn("--Debbug-- serviceLocator: " +serviceLocator);
	var service = serviceLocator.getWSINTFLUIGSOAP();
	var produtos = serviceProdutos.createWSCADSOLICITACAOCOMPRA();
	var arrayProdutos = serviceProdutos.createARRAYOFWSPRODUTOSSOLICITACAOCOMPRA();

	var numProces = getValue("WKNumProces");
	var cardId = getValue("WKCardId");
	// Chamada
	var cst1 = DatasetFactory.createConstraint("metadata#active", true, true, ConstraintType.MUST);
	var cst2 = DatasetFactory.createConstraint("metadata#id", cardId, cardId, ConstraintType.MUST);

	var constraints = new Array(cst1, cst2);
	var datasetPrincipal = DatasetFactory.getDataset("dsFormSolicitacaoCompras", null, constraints, null);
	log.warn("--Debbug-- datasetPrincipal");
	log.warn("--Debbug-- numProces: "+numProces);
	log.warn("--Debbug-- cardId: "+cardId);
	log.warn("--Debbug-- datasetPrincipal.rowsCount: " +datasetPrincipal.rowsCount);

	if (datasetPrincipal.rowsCount > 0) {

		var documentId = datasetPrincipal.getValue(0, "metadata#id");
		var documentVersion = datasetPrincipal.getValue(0, "metadata#version");
		var c1 = DatasetFactory.createConstraint("tablename", 'tbSolCompras', 'tbSolCompras', ConstraintType.MUST);
		var c2 = DatasetFactory.createConstraint("metadata#id", documentId, documentId, ConstraintType.MUST);
		var c3 = DatasetFactory.createConstraint("metadata#version", documentVersion, documentVersion, ConstraintType.MUST);

		var constraintsFilho = new Array(c1, c2, c3);
		var datasetFilho = DatasetFactory.getDataset("dsFormSolicitacaoCompras", null, constraintsFilho, null);

		log.warn("--Debbug-- datasetFilho.rowsCount : " +datasetFilho.rowsCount);

		if (datasetFilho.rowsCount > 0 ) {
			for (var j = 0; j < datasetFilho.rowsCount; j++) {
				var structProduto = serviceProdutos.createWSPRODUTOSSOLICITACAOCOMPRA();

				//var cCusto = datasetFilho.getValue(j, "zoomCr");
				//var codCusto = cCusto.split('-');
				//var cProduto = datasetFilho.getValue(j, "codProduto");
				//var codProduto = cProduto.split('-');
				structProduto.setCCCUSTO(datasetPrincipal.getValue(0, "centroCusto_id"));
				structProduto.setCMOTIVOSOL(datasetFilho.getValue(j, "obsProduto"));
				structProduto.setCPRODUTO(datasetFilho.getValue(j, "codProduto"));
				structProduto.setCUM(datasetFilho.getValue(j, "unidade"));
				// structProduto.setCXIMG(datasetFilho.getValue(j, "publiclink"));
				structProduto.setCXOBS(datasetFilho.getValue(j, "obsProduto"));
				structProduto.setNPRECO(0);
				var qtde = datasetFilho.getValue(j, "qtde");
				var qtdeProd = qtde.replace('.', '').replace(',', '.');
				//teste.replace(/[^\d]+/g,'')
				log.warn("--Debbug-- qtdeProd: "+qtdeProd);
				//var NVALORTIT = new java.math.BigInteger(valor2);
				var qtdeProduto = new java.math.BigDecimal(qtdeProd);
				log.warn("--Debbug-- qtdeProduto: "+qtdeProduto);
				structProduto.setNQUANTIDADE(qtdeProduto);
				arrayProdutos.getWSPRODUTOSSOLICITACAOCOMPRA().add(structProduto);
			}
			produtos.setPRODUTOS(arrayProdutos);
			produtos.setCEMPRESA('01');
			produtos.setCPARFIL("0101");
			produtos.setCXIMG(datasetPrincipal.getValue(0, "publiclink"));
			produtos.setCCOMPRADOR(datasetPrincipal.getValue(0, "codComprador"));

			var today = new Date();
			var year = today.getFullYear();
			var month = (today.getMonth() + 1) < 10 ? '0' + (today.getMonth() + 1)	: (today.getMonth() + 1);
			var day = today.getDate() < 10 ? '0' + today.getDate() : today.getDate();
			var currentDate = day + '/' + month + '/' + year;
			var currentDateInv = year + month + day;

			produtos.setCEMISSAO(currentDateInv);
			produtos.setCFILENT("0101");
			produtos.setCIDFLUIG(numProces);
			//produtos.setCSOLICITANTE(datasetPrincipal.getValue(0, "idUserProtheus"));
			produtos.setCSOLICITANTE("000012");

			try {
				log.warn("--Debbug-- Cadastro Solicicao produtos: " + produtos);
				var result = service.cadastraSOLICITACAOCOMPRA(produtos);
				log.warn("--Debbug-- Cadastro Solicicao result: " +result);
				if (result.isLRET()) {
					hAPI.setCardValue('solicitacaoCompra', result.getCNUMSOL());
					log.warn("--Debbug-- solicitacaoCompra: " + result.getCNUMSOL());
				} else {
					throw 'Solicitação de Compras. Erro na geração: ' + result.getCERROS();
					log.warn("--Debbug-- Erro: " +  result.getCERROS());
				}
			} catch (erro){
				log.warn("--Debbug-- erro: " +erro);
				log.warn("--Debbug-- erro --linha: " +erro.lineNumber);
				throw 'Tente novamente!';
			}
		} else {
			throw 'Solicitação de Compra sem Pedido de Compra gerado. Favor verificar.';
		}
	} else {
		throw 'Não encontrado o formulário Id: ' + cardId + ' da Solicitação de Compras - ' + numProces +'.';
	}

	return true;
}

function servicetask50(attempt, message) {
	var nSol = hAPI.getCardValue("solicitacaoCompra");
	var c1 = DatasetFactory.createConstraint("NUMEROSOLICITACAO", nSol, nSol, ConstraintType.MUST);
	var pedidosdaSol = DatasetFactory.getDataset("dsConsultaPedidosDaSolicitacao", null, new Array(c1), null);
	log.warn("--Debbug-- DATASET SERVICETASK50 = " + pedidosdaSol);
	var idSolicitante = hAPI.getCardValue("idSolicitante");
	// var idSolicitante = "010101";
	log.warn("-- Debbug ID Sol. " + idSolicitante);
	// "System:Auto"
	var today = new Date();
	var year = today.getFullYear();
	var month = (today.getMonth() + 1) < 10 ? '0' + (today.getMonth() + 1)	: (today.getMonth() + 1);
	var day = today.getDate() < 10 ? '0' + today.getDate() : today.getDate();
	var hour = today.getHours() < 10 ? '0' + today.getHours() : today.getHours();
	var minute = today.getMinutes() < 10 ? '0' + today.getMinutes() : today.getMinutes();
	var second = today.getSeconds() < 10 ? '0' + today.getSeconds() : today.getSeconds();
	var currentHour = hour + ":" + minute + ":" + second;
	var currentDate = day + '/' + month + '/' + year;
	var currentTime = currentDate + "  " + currentHour;

	// Adicionar Histórico de Pedidos Gerados no formulário Solicitação de Compras.
	if(pedidosdaSol.rowsCount > 0) {
		for(var i=0; i < pedidosdaSol.rowsCount; i++) {
			var codigoPedidoProtheus = pedidosdaSol.getValue(i, "NUMEROPEDIDODECOMPRA");
			var codigoSolicitacaoProtheus = pedidosdaSol.getValue(i, "NUMEROSOLICITACAO");
			var filialEntrega = hAPI.getCardValue('zoomFilialEntrega');

			try {
				var ECMWorkflowEngine = ServiceManager.getService('ECMWorkflowEngineService');
				log.warn("--Debbug-- ECMWorkflowEngine: " +ECMWorkflowEngine);
				var serviceLocator = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.ECMWorkflowEngineServiceService');
				log.warn("--Debbug-- serviceLocator: " +serviceLocator);
				var service = serviceLocator.getWorkflowEngineServicePort();
				log.warn("--Debbug-- service: " +serviceLocator);
				var serviceObj= ECMWorkflowEngine.instantiate('net.java.dev.jaxb.array.ObjectFactory');
				log.warn("--Debbug-- serviceObj: " +serviceObj);
				var serviceAttArray = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.ProcessAttachmentDtoArray');
				var serviceTaskArray = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.ProcessTaskAppointmentDtoArray');
				var colleagueIni = serviceObj.createStringArray();
				var cardData = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.KeyValueDtoArray');
				var campo = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.KeyValueDto');
				campo.setKey('numeroPedidoProtheus');
				campo.setValue(codigoPedidoProtheus);
				cardData.getItem().add(campo);
				var campo = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.KeyValueDto');
				campo.setKey('numeroSolicitacaoProtheus');
				campo.setValue(codigoSolicitacaoProtheus);
				cardData.getItem().add(campo);
				var campo = ECMWorkflowEngine.instantiate('com.totvs.technology.ecm.workflow.ws.KeyValueDto');
				campo.setKey('localEntrega');
				campo.setValue(filialEntrega);

				cardData.getItem().add(campo);
				colleagueIni.getItem().add("System:Auto");
				var result = service.startProcessClassic("arthur.barros@totvs.com.br", "3009Artfbgyn@", 1, "PedidoDeCompra", 0, colleagueIni, "Iniciado Automaticamente", idSolicitante, true, serviceAttArray, cardData,serviceTaskArray,false);

				log.warn('--Debbug-- Result: ' + result);
				log.warn('--Debbug-- Result ID: '+ result.getItem().get(4).getValue());
				log.warn('--Debbug-- Result Key: '+ result.getItem().get(4).getKey());

				var c1 = DatasetFactory.createConstraint('CGETIDFLUIG',result.getItem().get(4).getValue(), result.getItem().get(4).getValue(), ConstraintType.MUST);
				var c2 = DatasetFactory.createConstraint('CIDPROTHEUS', codigoPedidoProtheus, codigoPedidoProtheus, ConstraintType.MUST);
				var datasetDsConsultaPedidosDaSolicitacao = DatasetFactory.getDataset('dsInserirCodigoFluigPedidoCompra', null, new Array(c1, c2), null);

				var childData = new java.util.HashMap();
				childData.put("nPedido", pedidosdaSol.getValue(i, 'NUMEROPEDIDODECOMPRA'));
				childData.put("nSolFluig",  result.getItem().get(4).getValue());
				childData.put("descPedido", 'Pedido Gerado no Protheus');
				childData.put("dataPedido", currentTime);
				hAPI.addCardChild('tablePedidoCompra', childData);

			} catch(err) {
				log.warn('-- DEBBUG SOLCOMPRAS TASK 50: '+ err);
				log.warn('-- DEBBUG SOLCOMPRAS TASK 50 Linha: ' + err.lineNumber);
			}
		}
	} else {
		log.warn('-- DEBBUG SOLCOMPRAS TASK 50: Não há pedidos de compras no dataset.');
	}

	return true;
}
